$(document).ready(function(){
    $("#password_show_button").mouseup(function(){
        $("#password_show").attr("type", "password");
    });
    $("#password_show_button").mousedown(function(){
        $("#password_show").attr("type", "text");
    });
    $("#confirm_password_show_button").mouseup(function(){
        $("#confirm_password_show").attr("type", "password");
    });
    $("#confirm_password_show_button").mousedown(function(){
        $("#confirm_password_show").attr("type", "text");
    });
});